package org.firstinspires.ftc.teamcode.mechanisms.subsystems.intake;import com.qualcomm.robotcore.hardware.DcMotor;import com.qualcomm.robotcore.hardware.HardwareMap;import org.firstinspires.ftc.teamcode.Configuration;import org.firstinspires.ftc.teamcode.helpers.Globals;public class IntakeHelper {    public static DcMotor intake;    public static void init(HardwareMap hardwareMap) {        if (Configuration.intake.itemActive) {            intake = hardwareMap.get(DcMotor.class, Configuration.intake.itemName);            intake.setMode(DcMotor.RunMode.RUN_WITHOUT_ENCODER);        }    }    public static void start() {        intake.setPower(Globals.intakeSpeed);    }    public static void stop() {        intake.setPower(0);    }    public static void reverse() {        intake.setPower(-Globals.intakeSpeed);    }}